INSERT INTO TRACK_BARCODE ( TENANT_ID, FACTORY_ID, BARCODE, BARCODE_ID, BARCODE_RULE_DICT_ID, BARCODE_STATUS_DICT_ID, PRODUCT_TYPE_DICT_ID, PRODUCT_ID, MATERIAL_ID, BARCODE_PACKBOX_ID, BATCH_NUMBER, PALLET_ID, BILL_TYPE_DICT_ID, BILL_ID, BILL_ITEM_ID, BILL_STATUS_DICT_ID, SUPPLIER_ID, WAREHOUSE_ID, LOCATION_ID, BILL_WORKORDER_ID, TASK_PROCEDURE_ID, PROCESS_ID, PROCESS_NODE_ID, INSTRUCT_ID, INSTRUCT_TYPE_DICT_ID, WORKSHOP_ID, WORKSHOP_AREA_ID, WORKSTATION_ID, EQUIP_WORK_ID, EQUIP_WORK_STACK_ID, EQUIP_FIXTURE_ID, EQUIP_FIXTURE_STACK_ID, COMPOSE_MATERIAL_ID, FROM_BARCODE, FROM_MATERIAL_ID, COMPOSE_BARCODE, QUANTITY, QUANTITY_STOCK, OPERATION_STATUS_DICT_ID, REMARK
